if (FateManager.WithinFate && FateManager.GetFateById(572).MaxLevel < Core.Me.ClassLevel && !Core.Me.IsLevelSynced)
{
  if (Core.Me.IsMounted)
  {
    await CommonTasks.StopAndDismount();
  }

  Logging.Write("Attempting to Level Sync");
  await Coroutine.Sleep(3000);
  ToDoList.LevelSync();
  await Coroutine.Sleep(500);
}

