CodeChunks.CodeChunk:

        var _msg = String.Empty;
#if RB_CN
        _msg = "";
#else
        _msg = "Complete the next objective manually.";
#endif
        Core.OverlayManager.AddToast(() => _msg, TimeSpan.FromMilliseconds(25000), System.Windows.Media.Color.FromRgb(29,213,226), System.Windows.Media.Color.FromRgb(13,106,175), new System.Windows.Media.FontFamily("Gautami"));


var _npcIds = new uint[] { 1025063, 1025064, 1025065, 1025066 };

await CommonTasks.StopAndDismount();

var activeId = 0u;
foreach(var id in _npcIds)
{
  var _gameObj = GameObjectManager.GetObjectByNPCId(id);
  if (_gameObj != null && _gameObj.IsTargetable)
  {
    activeId = id;
    break;
  }
}

var _obj = GameObjectManager.GetObjectByNPCId(activeId);
if (_obj.IsVisible)
{
  _obj.Target();
  if (activeId == 1025063 || activeId == 1025064)  {  ChatManager.SendChat("/soothe");  }
  else {  ChatManager.SendChat("/rally");  }
}

while (_obj != null && _obj.IsVisible && _obj.IsTargetable)
{
  if (Talk.DialogOpen)  {  Talk.Next();  }
  await Coroutine.Yield();
}
