foreach (var plugin in PluginManager.Plugins.Where(p => p.Plugin.Name == "Enable Flight" || p.Plugin.Name == ""))
{
  try
  {
    if (!plugin.Enabled)  {  break;  }  else  {  plugin.Enabled = false;  }
  }
  catch (Exception ex) {  }
}


